<!--
 @component
 Buttons trigger an action such as submitting a form or showing/hiding an interface component.
-->
<script>
/** @type {'primary' | 'secondary' | 'outline' | 'ghost' | 'destructive'}  */
export let variant = 'primary';

/** @type {boolean}  */
export let disabled = false;

/** @type {boolean}  */
// TODO: @jgibson - add loading state and styles
// export let loading = false;

/** @type {boolean}  */
export let focused = false;

/** @type {boolean}  */
export let active = false;
</script>

<button
	class={`m-button ${variant !== 'primary' ? `is-${variant}` : ''}`}
	class:is-active={active}
	class:is-focused={focused}
	on:click
	{disabled}
>
	{#if $$slots.icon}
		<div class="m-button-icon">
			<slot name="icon" />
		</div>
	{/if}
	<span class="m-button-label">
		<slot />
	</span>
</button>
