<!--
 @component
 A wrapper component for adding a consistent margin between components.
-->
<script>
/** @type {import('../index').MSpaces} */
export let space = 'm';

/** @type {boolean} */
export let horizontal = false;

/** @type {boolean} */
export let level = false;

/** @type { 'start' | 'center' | 'end' }  align */
export let align = 'start';
</script>

<div
	class={`m-stack has-space-${space} ${align !== 'start' ? `is-align-${align}` : ''} `}
	class:is-horizontal={horizontal}
	class:has-space-between={level}
>
	<slot />
</div>
