<!--
 @component
 A versatile text component for sizing and styling UI typography
-->
<script>
/** @type {	'p' |'figcaption' | 'small' |'cite' | 'span' } */
export let as = 'p';

/** @type {import('../index').MFontSizes} */
export let size = 'm';

/** @type {import('../index').MTextVariants} */
export let variant = 'default';

/** @type {import('../index').MSizeMeasureContent} */
export let measure = '';

/** @type {boolean} */
export let truncate = false;

/** @type {boolean} */
export let tabular = false;

/** @type {boolean} */
export let currency = false;

</script>

<svelte:element
	this={as}
	class={`m-text is-size-${size} ${variant ? `is-${variant}` : ''} ${tabular ? `is-tabular` : ''} ${currency ? `is-currency` : ''}`}
	class:truncate
>
	<slot />
</svelte:element>
